import React from 'react';
import { Col, Card } from 'react-bootstrap';
import Image from 'react-bootstrap/Image';
import { useNavigate } from 'react-router-dom';
import { MENUITEM_ROUTE } from '../utils/consts';

const MenuItemCard = ({ menuitem }) => {
    const navigate = useNavigate();

    return (
        <Col md={3} className='mt-3 menuCard-list'>
            <Card
                border="light"
                onClick={() => navigate(MENUITEM_ROUTE + '/' + menuitem.id)}
                className="shadow-sm d-flex flex-column align-items-center menuCard"
            >
                <Image
                    height={200}
                    src={process.env.REACT_APP_API_URL + menuitem.image}
                    alt={menuitem.name}
                    style={{ width: '100%', height: '200px', flexShrink: '0', objectFit: 'cover', borderRadius: '10px 10px 0 0'}} 
                />
                <Card.Body className="d-flex flex-column justify-content-between align-items-center menuCard-text">
                    <Card.Title className='text-center'>{menuitem.name}</Card.Title>
                    <div className='text-black-50 d-flex justify-content-center align-items-center fs-6'>
                        <div>{menuitem.description.slice(0, 20) + "..."}</div>
                    </div>
                </Card.Body>
            </Card>
        </Col>
    );
};

export default MenuItemCard;
