import { observer } from 'mobx-react-lite';
import React, { useContext } from 'react';
import { Context } from '..';
import ListGroup from 'react-bootstrap/ListGroup';
import { BImage, Row } from 'react-bootstrap';
import { Button, CardImg } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { REST_ROUTE } from "../utils/consts";

const TypeBar = observer(() => {
    const { menuitem } = useContext(Context);
    const navigate = useNavigate();

    const Menu = () => {
        menuitem.setSelectedType({});
        navigate(REST_ROUTE);
    };

    return (
        <div className="d-flex flex-col row">
            <button className='menuBtn' onClick={Menu}>Меню</button>
            <ListGroup as="ol" numbered className="customList mt-4">
                {menuitem.types.map(type => (
                    <ListGroup.Item
                        as="li"
                        className="customList-item"
                        style={{ cursor: 'pointer', transition: 'background-color 0.2s' }}
                        active={type.id === menuitem.selectedType.id}
                        onClick={() => menuitem.setSelectedType(type)}
                        key={type.id}
                        onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#e9ecef'}
                        onMouseLeave={(e) => e.currentTarget.style.backgroundColor = ''}
                    >
                        <span>{type.name}</span>
                        <div className='menuListItem-img' data-img={type.id}>
                            <Image
                                src={type.image_url}
                                alt={type.image_url}
                            />
                        </div>
                    </ListGroup.Item>
                ))}
            </ListGroup>
        </div>
    );
});

export default TypeBar;
