import React, { useContext } from 'react';
import { Context } from "../index";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import { ADMIN_ROUTE, LOGIN_ROUTE, ORDERS_ROUTE, REST_ROUTE, RESTINFO_ROUTE, WAITER_ROUTE } from "../utils/consts";
import { Button } from "react-bootstrap";
import { observer } from "mobx-react-lite";
import Container from "react-bootstrap/Container";
import { useNavigate } from 'react-router-dom';

const NavBar = observer(() => {
    const { user } = useContext(Context);
    const navigate = useNavigate();

 //   console.log("isAuth "+ user.isAuth)
 //   console.log("isAdmin "+ user.isAdmin)
 //   console.log("isWaiter "+ user.isWaiter)
 //   console.log(user)
    const logOut = () => {
        user.setUser({})
        user.setIsAuth(false)
        user.setIsAdmin(false)
        user.setIsWaiter(false);
        navigate(LOGIN_ROUTE)
        localStorage.removeItem('token')
        
    };

    return (
        <Navbar bg="primary" data-bs-theme="dark" className=''>
            <Container>
                <Navbar.Brand style={{ cursor: 'pointer' }} onClick={() => navigate(REST_ROUTE)}>Ресторан "Малорита"</Navbar.Brand>
                <Nav className="me-auto">
                    <Nav.Link onClick={() => navigate(RESTINFO_ROUTE)}>О нас</Nav.Link>
                </Nav>
                {user.isAuth ? (
                    <Nav className="mt-auto">
                        
                        {user.isWaiter || user.isAdmin ? (
                            <Button variant="outline-light" onClick={() => navigate(WAITER_ROUTE)} className="me-2">
                                Панель официанта
                            </Button>
                        ) : (
                            <Button variant="outline-light" onClick={() => navigate(ORDERS_ROUTE)} className="me-2">
                                Заказы
                            </Button>
                        )}
        
                        {user.isAdmin && (
                            <Button variant="outline-light" onClick={() => navigate(ADMIN_ROUTE)} className="me-2">
                                Админ панель
                            </Button>
                        )}
                        <Button variant="outline-light" onClick={logOut}>
                            Выйти
                        </Button>
                    </Nav>
                ) : (
                    <Nav className="ms-auto">
                        <Button variant="outline-light" onClick={() => navigate(LOGIN_ROUTE)}>
                            Авторизация
                        </Button>
                    </Nav>
                )}
            </Container>
        </Navbar>
    );
});

export default NavBar;
