import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal'
import { Form, FormControl, Button } from 'react-bootstrap'
import { createType } from "../../http/menuitemAPI";

const CreateType = ({ show, onHide }) => {
    const [name, setName] = useState('')
    const [imageUrl, setImageUrl] = useState('')

    const addType = () => {
        try {
            createType({ 
                name: name,
                image_url: imageUrl  // Добавляем URL изображения
            }).then(data => {
                setName('')
                setImageUrl('')
                onHide()
            })
        }
        catch (e) {
            alert(e.response.data.message)
        }
    }

    return (
        <Modal show={show} onHide={onHide} size="lg" centered>
            <Modal.Header closeButton>
                <Modal.Title>Добавить новый тип</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group className="mb-3">
                        <Form.Label>Название типа</Form.Label>
                        <FormControl
                            value={name}
                            onChange={e => setName(e.target.value)}
                            placeholder='Например: Суши, Десерты'
                        />
                    </Form.Group>
                    
                    <Form.Group className="mb-3">
                        <Form.Label>Ссылка на изображение</Form.Label>
                        <FormControl
                            value={imageUrl}
                            onChange={e => setImageUrl(e.target.value)}
                            placeholder='https://example.com/image.jpg'
                        />
                    </Form.Group>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={addType}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    )
}

export default CreateType
