import React from 'react';
import { Col, Card } from 'react-bootstrap';
import Image from 'react-bootstrap/Image';
import { useNavigate } from 'react-router-dom';
import { MENUITEM_ROUTE } from '../utils/consts';

const MenuItemCard = ({ menuitem }) => {
    const navigate = useNavigate();

    return (
        <Col md={3} className='mt-3'>
            <Card
                style={{ width: '100%', height: '300px', cursor: 'pointer', transition: 'transform 0.2s' }}
                border="light"
                onClick={() => navigate(MENUITEM_ROUTE + '/' + menuitem.id)}
                className="shadow-sm d-flex flex-column align-items-center"
            >
                <Image
                    width={150}
                    height={150}
                    src={process.env.REACT_APP_API_URL + menuitem.image}
                    alt={menuitem.name}
                    style={{ borderRadius: '8px 8px 0 0', objectFit: 'cover', height: '150px', margin: '20px auto 0' }} 
                />
                <Card.Body className="d-flex flex-column justify-content-between align-items-center">
                    <Card.Title className='mt-2 text-center'>{menuitem.name}</Card.Title>
                    <div className='text-black-50 d-flex justify-content-center align-items-center fs-6 mt-2'>
                        <div>{menuitem.description.slice(0, 20) + "..."}</div>
                    </div>
                </Card.Body>
            </Card>
        </Col>
    );
};

export default MenuItemCard;
