import React, { useContext, useState } from 'react';
import { Button, Card, Col, Container, Image, Row } from 'react-bootstrap';
import CreateReview from '../components/modals/CreateReview';
import GetReviews from '../components/modals/GetReviews';
import info from '../assets/info.jpg'
import { Context } from '..';

const RestInfo = () => {
    const [reviewVisible, setReviewVisible] = useState(false);
    const [getReviewVisible, setGetReviewVisible] = useState(false);
    const { user } = useContext(Context);
    
    const restinfo = {
        id: 1,
        name: "Малорита",
        address: "ул. Лермонтова д. 11/А",
        email: "malorita@mail.ru",
        phone: "+375298446589",
        description: "Лучший ресторан Брестской области, в котором вы сможете попробовать то, от чего вы будете в восторге!"
    };

    return (
        <Container className='mt-5'>
            <Card className='shadow-lg'>
                <Row className='g-0'>
                    <Col md={4} className='d-flex'> {/* Добавлены внутренние отступы */}
                        <Image
                            height={300}
                            src={info}
                            alt='Ресторан Малорита'
                            className='img-fluid rounded-start'
                        />
                    </Col>

                    <Col md={8}>
                        <Card.Body>
                            <Card.Title className='text-primary pb-3 fs-2'>Ресторан "{restinfo.name}"</Card.Title>
                            <div className='d-flex mt-4'>
                                <Button 
                                    variant='outline-primary' 
                                    onClick={() =>  {if (!user.isAuth) {
                                        alert("Пожалуйста, авторизуйтесь для создания отзыва.");
                                        return;
                                      }else setReviewVisible(true)}}
                                    className='me-2'
                                >
                                    Оставить отзыв
                                </Button>
                                <Button 
                                    variant='outline-primary' 
                                    onClick={() => setGetReviewVisible(true)}
                                >
                                    Посмотреть все отзывы
                                </Button>
                            </div>
                        </Card.Body>
                        <Card.Text className='text-muted'>
                            <strong>Описание:</strong>
                            <span> {restinfo.description}</span>
                        </Card.Text>
                    </Col>
                </Row>
                <Row className='g-0 justify-content-center gap-4'>
                    <Col md={3} className='cardInfo'>
                        <Card.Text className='text-muted'>
                            <svg viewBox="0 -16 512 512" xmlns="http://www.w3.org/2000/svg">
                            <path d="m437 60h-15v60h-62.078125c.703125-4.929688 1.078125-9.933594 1.078125-15 0-57.898438-47.101562-105-105-105s-105 47.101562-105 105c0 5.066406.375 10.070312 1.078125 15h-62.078125v-60h-15c-41.355469 0-75 33.644531-75 75v285c0 33.085938 26.914062 60 60 60h392c33.085938 0 60-26.914062 60-60v-285c0-41.355469-33.644531-75-75-75zm-86.117188 90h71.117188v98.757812l-24.28125 24.128907-86.152344-67.003907 28.488282-38.011718c4.234374-5.648438 7.847656-11.628906 10.828124-17.871094zm-48.730468 300h-190.722656l83.664062-82.960938zm-170.632813-170.179688 71.386719-70.640624 53.09375 70.828124 37.570312-50.117187 82.691407 64.316406-99.097657 98.472657zm124.480469-249.820312c41.355469 0 75 33.644531 75 75 0 16.3125-5.167969 31.832031-14.949219 44.878906l-60.050781 80.113282-60.050781-80.113282c-9.78125-13.046875-14.949219-28.566406-14.949219-44.878906 0-41.355469 33.644531-75 75-75zm-94.882812 120c2.976562 6.242188 6.59375 12.222656 10.824218 17.871094l12.800782 17.074218-77.140626 76.339844-17.601562-13.636718v-97.648438zm-131.117188-15c0-19.554688 12.539062-36.238281 30-42.429688v267.429688c-10.921875 0-21.171875 2.933594-30 8.050781zm0 285c0-16.542969 13.457031-30 30-30h30v-104.398438l81.179688 62.902344-102.355469 101.496094h-8.824219c-16.542969 0-30-13.457031-30-30zm422 30h-100.867188l-50.054687-38.789062 120.921875-120.160157v98.949219h30c16.542969 0 30 13.457031 30 30s-13.457031 30-30 30zm30-81.949219c-8.828125-5.117187-19.078125-8.050781-30-8.050781v-267.429688c17.460938 6.191407 30 22.871094 30 42.429688zm0 0" fill="#000000" style="fill: rgb(2, 102, 67);"></path><path d="m256 150c24.8125 0 45-20.1875 45-45s-20.1875-45-45-45-45 20.1875-45 45 20.1875 45 45 45zm0-60c8.269531 0 15 6.730469 15 15s-6.730469 15-15 15-15-6.730469-15-15 6.730469-15 15-15zm0 0" fill="#000000" style="fill: rgb(2, 102, 67);"></path></svg>
                        
                        </Card.Text>
                        <Card.Text className='text-muted'>
                            {restinfo.address}
                            <strong>Адрес:</strong>
                        </Card.Text>
                    </Col>
                    <Col md={3} className='cardInfo'>
                        <Card.Text className='text-muted'>
                            <strong>Email:</strong>
                        </Card.Text>
                        <Card.Text className='text-muted'>
                            {restinfo.email}
                        </Card.Text>
                    </Col>
                    <Col md={3} className='cardInfo'>
                        <Card.Text className='text-muted'>
                            <strong>Телефон:</strong>
                        </Card.Text>
                        <Card.Text className='text-muted'>
                            {restinfo.phone}
                        </Card.Text>
                    </Col>
                </Row>
            </Card>
            <CreateReview show={reviewVisible} onHide={() => setReviewVisible(false)} />
            <GetReviews show={getReviewVisible} onHide={() => setGetReviewVisible(false)} />
        </Container>
    );
};

export default RestInfo;
